/* Base de données: base_site_commerce_ebenus */

DROP DATABASE IF EXISTS base_site_commerce_ebenus;
/*DROP USER IF EXISTS 'application'@'localhost';*/
CREATE DATABASE base_site_commerce_ebenus DEFAULT CHARACTER SET utf8;
/*CREATE USER 'application'@'localhost' IDENTIFIED BY 'passw0rd';*/
GRANT ALL ON base_site_commerce_ebenus.* TO 'application'@'localhost' IDENTIFIED BY 'passw0rd';
USE base_site_commerce_ebenus;


SET FOREIGN_KEY_CHECKS = 0; 
DROP TABLE IF EXISTS Utilisateur;
DROP TABLE IF EXISTS Role;
DROP TABLE IF EXISTS Adresse;
DROP TABLE IF EXISTS Produit;
DROP TABLE IF EXISTS Commande;
DROP TABLE IF EXISTS ArticleCommande;


 CREATE TABLE Role (
	idRole INTEGER PRIMARY KEY AUTO_INCREMENT,
	identifiant VARCHAR(50),
	description VARCHAR(500),
	marquerEffacer  TINYINT(1) DEFAULT 0,
	version int(15) DEFAULT 0
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;
 
 CREATE TABLE Utilisateur (
	idUtilisateur INTEGER PRIMARY KEY AUTO_INCREMENT,
	idRole INTEGER NOT NULL,
	civilite VARCHAR(100),
	prenom VARCHAR(100),
	nom VARCHAR(100),
	identifiant VARCHAR(100),
	motPasse VARCHAR(100),
	dateNaissance TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	dateCreation TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	dateModification TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	actif TINYINT(1) DEFAULT 1,
	marquerEffacer  TINYINT(1) DEFAULT 0,
	version INTEGER DEFAULT 1,
	CONSTRAINT `FK_Utilisateur_Role` FOREIGN KEY (idRole) referenceS Role (idRole)
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;


 CREATE TABLE Adresse (
	idAdresse INTEGER PRIMARY KEY AUTO_INCREMENT,
	idUtilisateur INTEGER NOT NULL,
	rue VARCHAR(100),
	codePostal VARCHAR(100),
	ville VARCHAR(100),
	pays VARCHAR(100),
	statut VARCHAR(10) DEFAULT 'P',
	typeAdresse VARCHAR(10) DEFAULT 'L',
	principale TINYINT(1) DEFAULT 0,
	version INTEGER DEFAULT 1,
	CONSTRAINT `FK_Adresse_Utilisateur` FOREIGN KEY (idUtilisateur) referenceS Utilisateur (idUtilisateur) ON DELETE CASCADE
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;
 
 CREATE TABLE Produit (
	idProduit INTEGER PRIMARY KEY AUTO_INCREMENT,
	reference VARCHAR(100),
	prix double,
	nom VARCHAR(100),
	description VARCHAR(500),
	stock int(11),
	active TINYINT(1),
	marquerEffacer  TINYINT(1) DEFAULT 0,
	version INTEGER DEFAULT 1
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;
 
 
 
 CREATE TABLE Commande (
	idCommande INTEGER PRIMARY KEY AUTO_INCREMENT,
	totalCommande double,
	idUtilisateur INTEGER NOT NULL,
	idAdresse INTEGER NOT NULL,
	statut VARCHAR(5),
	dateCommande TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	dateModification TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	version INTEGER DEFAULT 1,
	CONSTRAINT `FK_Commande_Utilisateur` FOREIGN KEY (idUtilisateur) referenceS Utilisateur (idUtilisateur) ON DELETE CASCADE,
	CONSTRAINT `FK_Commande_Adresse` FOREIGN KEY (idAdresse) referenceS Adresse (idAdresse) 
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;
 
 CREATE TABLE ArticleCommande (
	idArticleCommande INTEGER PRIMARY KEY AUTO_INCREMENT,
	idCommande INTEGER NOT NULL,
	idUtilisateur INTEGER NOT NULL,
	idAdresse INTEGER NOT NULL,
	idProduit INTEGER NOT NULL,
	totalArticleCommande double,
	reference VARCHAR(100),
	quantite int(11),
	statut VARCHAR(5),
	dateModification TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
	version INTEGER DEFAULT 1,
	CONSTRAINT `FK_ArticleCommande_Commande` FOREIGN KEY (idCommande) referenceS Commande (idCommande) ON DELETE CASCADE,
	CONSTRAINT `FK_ArticleCommande_Utilisateur` FOREIGN KEY (idUtilisateur) referenceS Utilisateur (idUtilisateur) ON DELETE CASCADE,
	CONSTRAINT `FK_ArticleCommande_Adresse` FOREIGN KEY (idAdresse) referenceS Adresse (idAdresse),
	CONSTRAINT `FK_ArticleCommande_Produit` FOREIGN KEY (idProduit) referenceS Produit (idProduit)
 )ENGINE=InnoDB DEFAULT CHARSET=utf8;




